sys_variable_value.value:
// The main purpose of this Action is to copy a catalog taxomomy into a service taxonomy
// Assumptions
//   1. the Catalog Category has a parent Catagory (this is pre-validated in flow)
//   2. This mapping will be created based on a category being added to a record producer 
//     LEVEL 1: Parent Catalog Category --> Service Portfolio Taxonomy Node
//     LEVEL 2: Catalog Item Category --> Business Service (classified under Taxonomy Node)
//     LEVEL 3: Record Producer --> Service Offering (classified under Business Service)
// Use Cases
//   1. Create all levels of objects and realtionships
//     -- Happens when the first Record Producer for the Parent Catalog Category and Catalog Item Category
//   2. Create one or more levels of the service structure
//     -- Happens when a new record producer is put under a pre-existing Parnet and/or Child Catalog Category
//     -- In this case the Taxonomy Node and/or Business Service Should already exist
//   3. A Record Producer changes names and no longer maps to the name of the Service Offering
//     -- This case is handled by ... 
//	     -- removing the linkage between the Record Producer and Service Offering
//       -- creating a new Service Offering that matches the name of the Record Producer
//       -- associating the new Service Offering with the Record Producer
//       NOTE: The old Service Offering, wiht the old name, is not deleted  
//   4. A Record Producer has more than 1 category
//     -- This case is **not** handled ...
//       -- The scipt will only take the first category so this can cause problems
//       -- Be sure to manually check the service portfolio to understand how the scipt behaved 
//   5. A Record Producer's is moved to a differnet Catalog Category
//     -- This case is **not** handled at this time ...
//       -- Manually handling would entail changing the parent of the offering to match the catalog category 
//       -- A second option would be to delete the existing service offering before moving the Record Producer
// 

(function execute(inputs, outputs) {

  // Get or create a taxonomy node the matches the parent catalog category 
  var taxNodeId = getTaxNode(inputs.triggerCat.sc_category.parent.title, inputs.taxLayer);

  // Get or create a Business Service the matches the catalog category 
  var busSvcId = getBusService(inputs.triggerCat.sc_category.title, taxNodeId);  

  // See if the catalog item is already attached to a Service Offering
  var assocRec = new GlideRecord('sc_cat_item_subscribe_no_mtom'); 
  assocRec.addQuery('sc_cat_item', inputs.triggerCat.sc_cat_item.sys_id);
  assocRec.query();

  // There should be only 1 association between a catalog item and a service offering if any
  if(assocRec.getRowCount() > 0){
    // Get a reference to the current offering
    assocRec.next();
    outputs.svcoffering = assocRec.service_offering.sys_id;
    outputs.offeringparent = busSvcId;
    updateOffering (assocRec.service_offering.sys_id, inputs.triggerCat.sc_cat_item.name, busSvcId);

  } else{ 
    // Get or create a Service Offering the matches the record producer 
    var svcOffId = getSvcOffering(inputs.triggerCat.sc_cat_item.name, busSvcId);
    outputs.svcoffering = svcOffId;
    outputs.offeringparent = busSvcId;

    // Create the association of the Service Offering to Catalog Item  
    assocOfferingCatItem(inputs.triggerCat.sc_cat_item.sys_id, svcOffId);
  }

  // Create the CMDB Dependencies to the Service Offering
  createOffDepend(svcOffId, busSvcId, inputs.application_service);

  function updateOffering (objId, matchName, parentId) {

    var gr = new GlideRecord('service_offering');   
    gr.get(objId);

    gs.info("Rename the offering from: " + gr.name + " to: " + matchName + " with parent id: " + parentId );

    gr.name = matchName;
    gr.parent = parentId;
    gr.portfolio_status = "retired";
    gr.service_status = "retired";
    gr.update();

    if (gr.update() == null) {
      gs.info("the update failed!");
	}
  }  

  function getTaxNode (nodeName, taxLayerDef) {
	var taxNode = new GlideRecord('spm_taxonomy_node');
	taxNode.addQuery('name',nodeName);
	taxNode.addQuery('spm_taxonomy_layer',taxLayerDef.sys_id);
	taxNode.query();


	if (taxNode.getRowCount() == 0) {
	  taxNode.initialize();
	  taxNode.spm_service_portfolio = taxLayerDef.spm_service_portfolio.sys_id;
	  taxNode.name = nodeName;
	  taxNode.spm_taxonomy_layer = taxLayerDef.sys_id;
      gs.info("New Taxonomy Node: " + taxNode.name);
	  return taxNode.insert();	
	} else {
	  taxNode.next();
      gs.info("Found Taxonomy Node: " + taxNode.name);
	  return taxNode.sys_id;
	}

  }

  function getBusService (svcName, taxNode) {

	var newSvc = new GlideRecord('cmdb_ci_service');
	newSvc.addQuery('category', 'Business Service');
    newSvc.addQuery('spm_taxonomy_node', taxNode);
	newSvc.addQuery('name',svcName);
	newSvc.query();

	if (newSvc.getRowCount() == 0) {
	  newSvc.initialize();
	  newSvc.spm_service_portfolio = taxNode.spm_service_portfolio;
	  newSvc.name = svcName;
      newSvc.service_classification = 'Business Service';
	  newSvc.spm_taxonomy_node = taxNode;
	  newSvc.consumer_type = "External";
	  newSvc.used_for = 'Production';
      gs.info("New Business Service: " + newSvc.name);
	  return newSvc.insert();	
	} else {
	  newSvc.next();
      gs.info("Found Business Service: " + newSvc.name);
	  return newSvc.sys_id;
	}
  }

  function getSvcOffering (offName, parentSvc) {

	var newOff = new GlideRecord('service_offering');
	newOff.addQuery('parent', parentSvc);
	newOff.addQuery('name',offName);
	newOff.query();

	if (newOff.getRowCount() == 0) {
      gs.info("Creating SO ");
	  newOff.initialize();
	  newOff.parent = parentSvc;
	  newOff.name = offName;
      newOff.service_classification = 'Business Service';
      newOff.consumer_type = "External";
      gs.info("New Service Offering: " + newOff.name);
	  return newOff.insert();	
	} else {
      gs.info("Found Service Offering: " + newOff.name);
      newOff.next();
	  return newOff.sys_id;
	}
  }

  function createOffDepend (svcOffId, busSvcId, appSvcId) {
    var newDpnd = new GlideRecord('cmdb_rel_ci');
	// Create dependency to Parent Business Service
	newDpnd.addQuery('parent', busSvcId);
	newDpnd.addQuery('child',svcOffId);
	newDpnd.query();

	if (newDpnd.getRowCount() == 0) {
	  newDpnd.initialize();
	  newDpnd.parent = busSvcId;
	  newDpnd.child = svcOffId;
	  newDpnd.type.setDisplayValue("Contains::Contained by");
	  newDpnd.insert();	
	} 

    // Create dependency to Application Service
	newDpnd.initialize();
	newDpnd.addQuery('parent', svcOffId);
	newDpnd.addQuery('child',appSvcId.sys_id);
	newDpnd.query();

	if (newDpnd.getRowCount() == 0) {
	  newDpnd.initialize();
	  newDpnd.parent = svcOffId;
	  newDpnd.child = appSvcId.sys_id;
	  newDpnd.type.setDisplayValue("Depends on::Used by");
	  newDpnd.insert();	
	} 

  }

  function assocOfferingCatItem (catItemId, svcOffId) {
	var newAssoc = new GlideRecord('sc_cat_item_subscribe_no_mtom');
	newAssoc.addQuery('sc_cat_item', catItemId);
	newAssoc.addQuery('service_offering',svcOffId);
	newAssoc.query();

	if (newAssoc.getRowCount() == 0) {
	  newAssoc.initialize();
	  newAssoc.sc_cat_item = catItemId;
	  newAssoc.service_offering = svcOffId;
	  return newAssoc.insert();	
	} else {
	  newAssoc.next();
	  return newAssoc.sys_id;
	}
  }


})(inputs, outputs);

